pragma solidity 0.4.24;

import './GoForItToken.sol';

/**
 * @title Presale Token vesting contract
 * @author Sicos et al.
 */

contract PresaleVesting is Ownable {
    using SafeMath for uint;

    uint256 public endOfVestingPeriod;

    mapping (address => uint256) public presaleBalances;

    GoForItToken public token;

    /**
     * @dev constructor
     * @param _token Token contract address for GoForIt token
     */
    constructor(address _token, uint _endOfVestingPeriod) public {
        token = GoForItToken(_token);
        endOfVestingPeriod = _endOfVestingPeriod;
    }

    /**
     * @dev Adds balances for presale investors
     * @param _investors Address of a founder
     * @param _balances Number of tokens allocated to a founder

     */
     function addPresaleBalance(address[] _investors, uint[] _balances) public onlyOwner {
         require(_investors.length== _balances.length);
         for (uint i = 0; i < _investors.length; ++i) {
             presaleBalances[_investors[i]] = _balances[i];

         }
     }

    /**
     * @dev Allow company to unlock allocated tokens by transferring them whitelisted addresses.
     * Need to be called by each address
     */
    function withdrawToken() external {
        require(now >= endOfVestingPeriod);

        uint tokensToTransfer = presaleBalances[msg.sender];
        presaleBalances[msg.sender] = 0;

        require(token.transfer(msg.sender, tokensToTransfer));
    }

    /**
     * @dev allow for selfdestruct possibility and sending funds to owner if presale investors didn't
     */
    function destruct() public onlyOwner {
        require(now >= endOfVestingPeriod.add(365 days));
        uint256 balance = token.balanceOf(this);

        if (balance > 0) {
            token.transfer(owner, balance);
        }

        selfdestruct(owner);
    }
}

